import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import * as d3 from 'd3';

const DynamicGraph = () => {
  const [jokes, setJokes] = useState([]);
  const [diseaseNames, setDiseaseNames] = useState([]);
  const [selectedDisease, setSelectedDisease] = useState('');
  const svgRef = useRef();
  const [tooltip, setTooltip] = useState({ display: false, top: 0, left: 0, content: '' });

  // Function to generate random values
  const generateRandomValue = () => Math.floor(Math.random() * 9000) + 1;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/data');
        setJokes(response.data);

        const diseases = response.data.flatMap((joke) =>
          joke.entry.map((entry) => {
            const code = entry?.resource?.code;
            return code?.text || 'Unknown';
          })
        );

        const uniqueDiseases = [...new Set(diseases)];
        setDiseaseNames(uniqueDiseases);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (selectedDisease && jokes.length > 0) {
      const svg = d3.select(svgRef.current);

      svg.selectAll('*').remove(); // Clear existing content

      // Find the random value for the selected disease
      const diseaseEntry = jokes.find(
        (joke) => joke.entry[0].resource.code.text === selectedDisease
      );

      

      const width = 800;
      const height = 600;
      const margin = { top: 40, right: 40, bottom: 70, left: 70 };

      const x = d3.scaleBand()
        .domain(data.map((d, i) => i + 1))
        .range([margin.left, width - margin.right])
        .padding(0.1);

      const y = d3.scaleLinear()
        .domain([10, d3.max(data, (d) => d.count + 10)])
        .nice()
        .range([height - margin.bottom, margin.top]);

      // Draw x-axis with label
      svg.append('g')
        .attr('transform', `translate(0,${height - margin.bottom})`)
        .call(d3.axisBottom(x).tickSizeOuter(0))
        .append('text') // Add x-axis label
        .attr('x', width / 2)
        .attr('y', margin.bottom / 2)
        .attr('text-anchor', 'middle')
        .style('font-size', '14px')
        .text('Year');

      // Draw y-axis with label
      svg.append('g')
        .attr('transform', `translate(${margin.left}, 0)`)
        .call(d3.axisLeft(y))
        .append('text') // Add y-axis label
        .attr('transform', 'rotate(-90)')
        .attr('x', -(height / 2))
        .attr('y', -margin.left / 2)
        .attr('text-anchor', 'middle')
        .style('font-size', '14px')
        .text('Occurrences');

      // Draw the bars with tooltip interaction
      svg.selectAll('.bar')
        .data(data)
        .enter()
        .append('rect')
        .attr('class', 'bar')
        .attr('x', (d, i) => x(i + 1))
        .attr('y', (d) => y(d.count))
        .attr('width', x.bandwidth())
        .attr('height', (d) => height - margin.bottom - y(d.count))
        .attr('fill', 'steelblue')
        .on('mouseover', (event, d) => {
          setTooltip({
            display: true,
            top: event.pageY - 10,
            left: event.pageX + 10,
            content: `Random Value: ${d.count}`,
          });
        })
        .on('mouseout', () => setTooltip({ display: false, content: '' }));
    }
  }, [selectedDisease, jokes]);

  return (
    <div>
      <h2>Select a Disease</h2>
      <select onChange={(e) => setSelectedDisease(e.target.value)} value={selectedDisease}>
        <option value="">Select a disease</option>
        {diseaseNames.map((disease, index) => (
          <option key={index} value={disease}>
            {disease}
          </option>
        ))}
      </select>

      {selectedDisease && (
        <div>
          <h3>Occurrence of {selectedDisease}</h3>
          <svg ref={svgRef} width="800" height="600"></svg>
          {tooltip.display && (
            <div
              className="tooltip"
              style={{
                position: 'absolute',
                top: tooltip.top,
                left: tooltip.left,
                padding: '5px',
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                border: '1px solid #000',
                borderRadius: '3px',
                pointerEvents: 'none',
                fontSize: '12px',
              }}
            >
              {tooltip.content}
            </div>
          )}
          {/* Chart Description */}
          <div style={{ marginTop: '20px', fontSize: '14px', fontFamily: 'Arial, sans-serif' }}>
            <h3>Chart Description</h3>
            <p>This chart visualizes the occurrence of {selectedDisease} over the years.</p>
            <ul>
              <li>
                <strong>X-axis:</strong> Represents the years
              </li>
              <li>
                <strong>Y-axis:</strong> Represents the number of occurrences
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default DynamicGraph;
